mapper.select:
SELECT r.*, i.item_name, i.item_code
FROM exam_result r
LEFT JOIN exam_item i ON r.item_id = i.id


        AND r.record_id = #{recordId}


        AND r.item_id = #{itemId}


        AND r.is_abnormal = #{resultType}


        AND r.review_status = #{reviewStatus}


ORDER BY r.create_time DESC
